import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { Users, Trophy, Target, Award, CheckCircle, XCircle, Plus, Edit2, Trash2, UserPlus, BarChart3, Shield, LogOut, Mail, Lock, AlertCircle, X } from 'lucide-react';

// Composant Login
const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = () => {
    if (!email || !password) {
      setError('Veuillez remplir tous les champs');
      return;
    }

    // Pour tester : admin@example.com / password
    if (email === 'admin@example.com' && password === 'password') {
      onLogin({ name: 'Admin', email: 'admin@example.com' });
    } else {
      setError('Email ou mot de passe incorrect');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md p-8">
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-indigo-100 rounded-full mb-4">
            <Shield className="w-8 h-8 text-indigo-600" />
          </div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Admin Dashboard</h1>
          <p className="text-gray-600">Connectez-vous pour g√©rer la plateforme</p>
        </div>

        {error && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg flex items-start gap-3">
            <AlertCircle className="w-5 h-5 text-red-600 flex-shrink-0 mt-0.5" />
            <p className="text-sm text-red-700">{error}</p>
          </div>
        )}

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
            <div className="relative">
              <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent outline-none"
                placeholder="admin@example.com"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Mot de passe</label>
            <div className="relative">
              <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent outline-none"
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              />
            </div>
          </div>

          <button
            onClick={handleSubmit}
            className="w-full bg-indigo-600 text-white py-3 rounded-lg font-medium hover:bg-indigo-700 transition"
          >
            Se connecter
          </button>
        </div>

        <div className="mt-6 text-center text-sm text-gray-600">
          <p>Test: admin@example.com / password</p>
        </div>
      </div>
    </div>
  );
};

// Composant Header
const Header = ({ user, onLogout }) => {
  return (
    <header className="bg-white shadow-lg border-b-4 border-indigo-600">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <div className="flex items-center gap-3">
          <Shield className="w-10 h-10 text-indigo-600" />
          <div>
            <h1 className="text-2xl font-bold text-gray-800">Admin Dashboard</h1>
            <p className="text-sm text-gray-600">Gestion compl√®te de la plateforme</p>
          </div>
        </div>

        <div className="flex items-center gap-4">
          {user && (
            <div className="text-right">
              <p className="font-medium text-gray-800">{user.name}</p>
              <p className="text-xs text-gray-600">{user.email}</p>
            </div>
          )}
          <button
            onClick={onLogout}
            className="flex items-center gap-2 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
          >
            <LogOut className="w-4 h-4" />
            D√©connexion
          </button>
        </div>
      </div>
    </header>
  );
};

// Composant Navigation
const Navigation = ({ activeTab, setActiveTab }) => {
  const tabs = [
    { id: 'dashboard', label: 'Vue Globale', icon: BarChart3 },
    { id: 'teams', label: '√âquipes', icon: Users },
    { id: 'challenges', label: 'D√©fis', icon: Target },
    { id: 'scores', label: 'Scores', icon: CheckCircle },
    { id: 'requests', label: 'Demandes', icon: UserPlus }
  ];

  return (
    <div className="bg-white shadow-md border-b">
      <div className="max-w-7xl mx-auto px-6">
        <nav className="flex gap-1 overflow-x-auto">
          {tabs.map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-2 px-6 py-4 font-medium transition whitespace-nowrap ${
                activeTab === tab.id
                  ? 'text-indigo-600 border-b-4 border-indigo-600 bg-indigo-50'
                  : 'text-gray-600 hover:text-indigo-600 hover:bg-gray-50'
              }`}
            >
              <tab.icon className="w-5 h-5" />
              {tab.label}
            </button>
          ))}
        </nav>
      </div>
    </div>
  );
};

// Composant Modal
const Modal = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b flex justify-between items-center sticky top-0 bg-white">
          <h3 className="text-2xl font-bold text-gray-800">{title}</h3>
          <button onClick={onClose} className="p-2 hover:bg-gray-100 rounded-lg transition">
            <X className="w-6 h-6 text-gray-600" />
          </button>
        </div>
        <div className="p-6">{children}</div>
      </div>
    </div>
  );
};

// Application principale
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState('dashboard');

  const [teams, setTeams] = useState([
    { _id: '1', name: 'Les Champions', logo: 'üèÜ', score: 850, members: ['Alice', 'Bob'], badges: ['Premier'] },
    { _id: '2', name: 'Les Innovateurs', logo: 'üí°', score: 720, members: ['David'], badges: [] }
  ]);

  const [challenges, setChallenges] = useState([
    { _id: '1', title: 'D√©fi Principal 2025', type: 'principal', points: 500, deadline: '2025-03-15', description: 'Challenge majeur' }
  ]);

  const [pendingScores, setPendingScores] = useState([
    { _id: '1', teamName: 'Les Champions', challengeTitle: 'D√©fi Principal', points: 500, submittedBy: 'Alice', date: '2024-12-01' }
  ]);

  const [leaderRequests, setLeaderRequests] = useState([
    { _id: '1', name: 'Jean Dupont', email: 'jean@example.com', message: 'Je veux devenir leader', date: '2024-11-28' }
  ]);

  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('');
  const [formData, setFormData] = useState({});

  const handleLogin = (userData) => {
    setUser(userData);
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setUser(null);
    setIsLoggedIn(false);
  };

  const openModal = (type, data = {}) => {
    setModalType(type);
    setFormData(data);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setFormData({});
  };

  const handleSubmit = () => {
    if (modalType === 'team') {
      if (formData._id) {
        setTeams(teams.map(t => t._id === formData._id ? formData : t));
      } else {
        const newTeam = { _id: Date.now().toString(), ...formData, score: 0, members: [], badges: [] };
        setTeams([...teams, newTeam]);
      }
      alert('√âquipe ' + (formData._id ? 'modifi√©e' : 'cr√©√©e') + ' avec succ√®s');
    } else if (modalType === 'challenge') {
      if (formData._id) {
        setChallenges(challenges.map(c => c._id === formData._id ? formData : c));
      } else {
        const newChallenge = { _id: Date.now().toString(), ...formData };
        setChallenges([...challenges, newChallenge]);
      }
      alert('D√©fi ' + (formData._id ? 'modifi√©' : 'cr√©√©') + ' avec succ√®s');
    }
    closeModal();
  };

  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50">
      <Header user={user} onLogout={handleLogout} />
      <Navigation activeTab={activeTab} setActiveTab={setActiveTab} />

      <main className="max-w-7xl mx-auto px-6 py-8">

        {/* Dashboard */}
        {activeTab === 'dashboard' && (
          <div>
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Vue d'ensemble</h2>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
              <div className="bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl p-6 text-white shadow-lg">
                <Users className="w-8 h-8 mb-2 opacity-80" />
                <p className="text-sm opacity-90">√âquipes</p>
                <p className="text-3xl font-bold">{teams.length}</p>
              </div>
              <div className="bg-gradient-to-br from-purple-500 to-purple-600 rounded-xl p-6 text-white shadow-lg">
                <Target className="w-8 h-8 mb-2 opacity-80" />
                <p className="text-sm opacity-90">D√©fis Actifs</p>
                <p className="text-3xl font-bold">{challenges.length}</p>
              </div>
              <div className="bg-gradient-to-br from-amber-500 to-amber-600 rounded-xl p-6 text-white shadow-lg">
                <CheckCircle className="w-8 h-8 mb-2 opacity-80" />
                <p className="text-sm opacity-90">Scores en Attente</p>
                <p className="text-3xl font-bold">{pendingScores.length}</p>
              </div>
              <div className="bg-gradient-to-br from-green-500 to-green-600 rounded-xl p-6 text-white shadow-lg">
                <UserPlus className="w-8 h-8 mb-2 opacity-80" />
                <p className="text-sm opacity-90">Demandes Leader</p>
                <p className="text-3xl font-bold">{leaderRequests.length}</p>
              </div>
            </div>

            <div className="bg-white rounded-xl shadow-lg p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
                <Trophy className="w-6 h-6 text-yellow-500" />
                Classement des √âquipes
              </h3>
              <div className="space-y-3">
                {[...teams].sort((a, b) => b.score - a.score).map((team, idx) => (
                  <div key={team._id} className="flex items-center gap-4 p-4 bg-gray-50 rounded-lg">
                    <div className={`text-3xl font-bold ${idx === 0 ? 'text-yellow-500' : 'text-gray-400'}`}>
                      #{idx + 1}
                    </div>
                    <div className="text-3xl">{team.logo}</div>
                    <div className="flex-1">
                      <p className="font-bold text-gray-800">{team.name}</p>
                      <p className="text-sm text-gray-600">{team.members.length} membres</p>
                    </div>
                    <div className="text-right">
                      <p className="text-2xl font-bold text-indigo-600">{team.score}</p>
                      <p className="text-xs text-gray-500">points</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* √âquipes */}
        {activeTab === 'teams' && (
          <div>
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-3xl font-bold text-gray-800">Gestion des √âquipes</h2>
              <button
                onClick={() => openModal('team')}
                className="flex items-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition shadow-lg"
              >
                <Plus className="w-5 h-5" />
                Nouvelle √âquipe
              </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {teams.map(team => (
                <div key={team._id} className="bg-white rounded-xl shadow-lg p-6">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex items-center gap-3">
                      <div className="text-4xl">{team.logo}</div>
                      <div>
                        <h3 className="text-xl font-bold text-gray-800">{team.name}</h3>
                        <p className="text-sm text-gray-600">{team.members.length} membres</p>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => openModal('team', team)}
                        className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
                      >
                        <Edit2 className="w-5 h-5" />
                      </button>
                      <button
                        onClick={() => {
                          if (window.confirm('Supprimer cette √©quipe ?')) {
                            setTeams(teams.filter(t => t._id !== team._id));
                          }
                        }}
                        className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                      >
                        <Trash2 className="w-5 h-5" />
                      </button>
                    </div>
                  </div>

                  <div className="flex items-center justify-between p-3 bg-indigo-50 rounded-lg">
                    <span className="text-sm font-medium text-gray-700">Score Total</span>
                    <span className="text-2xl font-bold text-indigo-600">{team.score} pts</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* D√©fis */}
        {activeTab === 'challenges' && (
          <div>
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-3xl font-bold text-gray-800">Gestion des D√©fis</h2>
              <button
                onClick={() => openModal('challenge')}
                className="flex items-center gap-2 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition shadow-lg"
              >
                <Plus className="w-5 h-5" />
                Nouveau D√©fi
              </button>
            </div>

            <div className="space-y-4">
              {challenges.map(challenge => (
                <div key={challenge._id} className="bg-white rounded-xl shadow-lg p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <span className={`px-3 py-1 rounded-full text-xs font-bold ${
                          challenge.type === 'principal' ? 'bg-purple-100 text-purple-700' : 'bg-green-100 text-green-700'
                        }`}>
                          {challenge.type === 'principal' ? '‚≠ê Principal' : '‚ö° Mini'}
                        </span>
                        <h3 className="text-xl font-bold text-gray-800">{challenge.title}</h3>
                      </div>
                      <p className="text-gray-600 mb-3">{challenge.description}</p>
                      <div className="flex items-center gap-6 text-sm text-gray-600">
                        <span><Trophy className="w-4 h-4 inline" /> {challenge.points} points</span>
                        <span>üìÖ {challenge.deadline}</span>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => openModal('challenge', challenge)}
                        className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
                      >
                        <Edit2 className="w-5 h-5" />
                      </button>
                      <button
                        onClick={() => {
                          if (window.confirm('Supprimer ce d√©fi ?')) {
                            setChallenges(challenges.filter(c => c._id !== challenge._id));
                          }
                        }}
                        className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                      >
                        <Trash2 className="w-5 h-5" />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Scores */}
        {activeTab === 'scores' && (
          <div>
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Scores √† Valider</h2>
            <div className="space-y-4">
              {pendingScores.map(score => (
                <div key={score._id} className="bg-white rounded-xl shadow-lg p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h3 className="text-xl font-bold text-gray-800 mb-1">{score.teamName}</h3>
                      <p className="text-gray-600 mb-2">{score.challengeTitle}</p>
                      <p className="text-sm text-gray-600">Soumis par: {score.submittedBy}</p>
                    </div>
                    <div className="text-right">
                      <p className="text-3xl font-bold text-indigo-600 mb-4">{score.points} pts</p>
                      <div className="flex gap-2">
                        <button
                          onClick={() => {
                            setPendingScores(pendingScores.filter(s => s._id !== score._id));
                            setTeams(teams.map(t => t.name === score.teamName ? {...t, score: t.score + score.points} : t));
                            alert('Score valid√© !');
                          }}
                          className="flex items-center gap-2 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                        >
                          <CheckCircle className="w-5 h-5" />
                          Valider
                        </button>
                        <button
                          onClick={() => {
                            setPendingScores(pendingScores.filter(s => s._id !== score._id));
                            alert('Score rejet√©');
                          }}
                          className="flex items-center gap-2 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
                        >
                          <XCircle className="w-5 h-5" />
                          Rejeter
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Demandes */}
        {activeTab === 'requests' && (
          <div>
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Demandes de Leader</h2>
            <div className="space-y-4">
              {leaderRequests.map(request => (
                <div key={request._id} className="bg-white rounded-xl shadow-lg p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h3 className="text-xl font-bold text-gray-800 mb-1">{request.name}</h3>
                      <p className="text-indigo-600 mb-3">{request.email}</p>
                      <div className="bg-gray-50 rounded-lg p-4 mb-3">
                        <p className="text-gray-700 italic">"{request.message}"</p>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={() => {
                          setLeaderRequests(leaderRequests.filter(r => r._id !== request._id));
                          alert('Leader approuv√© !');
                        }}
                        className="flex items-center gap-2 px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                      >
                        <CheckCircle className="w-5 h-5" />
                        Approuver
                      </button>
                      <button
                        onClick={() => {
                          setLeaderRequests(leaderRequests.filter(r => r._id !== request._id));
                          alert('Demande rejet√©e');
                        }}
                        className="flex items-center gap-2 px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
                      >
                        <XCircle className="w-5 h-5" />
                        Rejeter
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

      </main>

      {/* Modal */}
      <Modal isOpen={showModal} onClose={closeModal} title={modalType === 'team' ? '√âquipe' : 'D√©fi'}>
        <div className="space-y-4">
          {modalType === 'team' && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Nom *</label>
                <input
                  type="text"
                  value={formData.name || ''}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
                  placeholder="Les Champions"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Logo *</label>
                <input
                  type="text"
                  value={formData.logo || ''}
                  onChange={(e) => setFormData({...formData, logo: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
                  placeholder="üèÜ"
                />
              </div>
            </>
          )}

          {modalType === 'challenge' && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Titre *</label>
                <input
                  type="text"
                  value={formData.title || ''}
                  onChange={(e) => setFormData({...formData, title: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Description *</label>
                <textarea
                  value={formData.description || ''}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
                  rows="3"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Type *</label>
                  <select
                    value={formData.type || 'mini'}
                    onChange={(e) => setFormData({...formData, type: e.target.value})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
                  >
                    <option value="mini">Mini</option>
                    <option value="principal">Principal</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Points *</label>
                  <input
                    type="number"
                    value={formData.points || ''}
                    onChange={(e) => setFormData({...formData, points: parseInt(e.target.value)})}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Date limite *</label>
                <input
                  type="date"
                  value={formData.deadline || ''}
                  onChange={(e) => setFormData({...formData, deadline: e.target.value})}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
                />
              </div>
            </>
          )}

          <div className="flex gap-3 pt-4">
            <button
              onClick={handleSubmit}
              className="flex-1 px-6 py-3 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 font-medium"
            >
              {formData._id ? 'Mettre √† jour' : 'Cr√©er'}
            </button>
            <button
              onClick={closeModal}
              className="px-6 py-3 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 font-medium"
            >
              Annuler
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default App;
